mapper.select:
SELECT S.UID, S.TRIALKEY , S.PROJECT, S.KIND, S.COMPANY, S.DEPARTMENT, S.NAME, S.RANK, S.IDNO, S.WORKTYPE1, S.WORKTYPE2, S.WORK, S.MAINSUB, S.FOODSTYLE, S.PERSONNO, S.GENDER, S.PHONE, S.ISPLAN, SD.TERMINAL, SD.NOTEBOOK AS LAPTOP, SD.MODEL_NAME AS MODELNM, SD.SERIAL_NUMBER AS SERIALNO, SD.FOREIGNER, SD.PASSPORT_NUMBER AS PASSPORTNO, SD.ORDER_STATUS, S.ISNONE, S.DELETE_YN FROM SCHECREW S LEFT OUTER JOIN SCHECREWDETAILINFO SD ON S.UID = SD.SCHEDULERINFOUID WHERE 1=1 AND S.UID IS NOT NULL AND S.TRIALKEY IS NOT NULL
AND S.PROJECT IS NOT NULL
AND S.TRIALKEY = #{ship}

SELECT UID, INOUTDATE, SCHEDULERINOUT, PERFORMANCEINOUT 
FROM SCHECREWINOUT
WHERE SCHECREWUID = #{uid}

	AND DATE_FORMAT(SI.INOUTDATE, '%Y-%m-%d')  >=  #{startDate}


	AND DATE_FORMAT(SI.INOUTDATE, '%Y-%m-%d')  <=  #{endDate}
	


ORDER BY INOUTDATE
