mapper.select:
SELECT S.UID, S.SCHEDULERINFOUID as schedulerInfoUid, S.TRIALKEY , S.PROJECT, S.KIND, S.COMPANY, S.DEPARTMENT, S.NAME, S.RANK, S.IDNO, S.WORKTYPE1, S.WORKTYPE2, S.WORK, S.MAINSUB, S.FOODSTYLE, S.PERSONNO, S.GENDER, S.PHONE, S.ISPLAN, SD.TERMINAL, SD.NOTEBOOK AS LAPTOP, SD.MODEL_NAME AS MODELNM, SD.SERIAL_NUMBER AS SERIALNO, SD.FOREIGNER, SD.PASSPORT_NUMBER AS PASSPORTNO, SD.ORDER_STATUS AS ORDERSTATUS, S.ISNONE, S.DELETE_YN FROM SCHECREW S LEFT OUTER JOIN SCHECREWDETAILINFO SD ON S.UID = SD.SCHEDULERINFOUID WHERE 1=1 AND S.UID IS NOT NULL AND S.TRIALKEY IS NOT NULL
AND S.PROJECT IS NOT NULL
AND S.TRIALKEY = #{ship}

SELECT UID, INOUTDATE, SCHEDULERINOUT, PERFORMANCEINOUT 
FROM SCHECREWINOUT
WHERE SCHECREWUID = #{uid}

	AND DATE_FORMAT(SI.INOUTDATE, '%Y-%m-%d')  >=  #{startDate}


	AND DATE_FORMAT(SI.INOUTDATE, '%Y-%m-%d')  <=  #{endDate}



ORDER BY INOUTDATE

select UID, SCHEDULERINFOUID as schedulerInfoUid, PROJ_NO as projNo, 
	TRIALKEY as trialKey, KIND as kind, DOMESTIC_YN as domesticYn, DEPT_NAME as department,
	MEAL_DATE as mealDate, ORDER_STATUS as orderStatus, ORDER_DATE as orderDate,
	ORDER_UID as orderUid, DEL_YN as deleteYn, COMMENT as comment, REG_DATE as inputDate, REG_USERNAME as inputUid
	from mobile_anchor_meal_info
	where 1=1 

	  and meal_date  >=  #{inDate}
	  and meal_date  <=  #{outDate}

	AND PROJ_NO  =  #{ship}

SELECT ANCHOR_MEAL_UID,  MEAL_TIME as resultMealTime, DEPT_NAME as department, proj_no as projNo,
	   MEAL_GUBUN as resultMealGubun, MEAL_DATE as resultMealDate, COUNT(*) as resultMealPlanQty
  FROM mobile_anchor_meal_userinfo
 where 1=1
   and meal_date  =  #{mealDate}
   and PROJ_NO  =  #{projNo} 
   AND DEPT_NAME  =  #{department}
 group by ANCHOR_MEAL_UID, MEAL_TIME, MEAL_GUBUN, MEAL_DATE
